function system_type(){
  TYPE_CHECK=$(dpkg -l | grep -o 'ubuntu-server ')
  if [[ $TYPE_CHECK == "" ]]; then
    SYSTEM_TYPE="Desktop"
  else
    SYSTEM_TYPE="Server"
  fi
}

function _task {
  if [[ $TASK != "" ]]; then
    printf "${OVERWRITE}${LGREEN} [✓]  ${LGREEN}${TASK}\n"
  fi
  TASK=$1
  printf "${LBLACK} [ ]  ${TASK} \n${LRED}"
}

# _cmd performs commands with error checking
function _cmd {
    > debug.log
    if eval "$1" 1> /dev/null 2> debug.log; then
        return 0 # success
    fi
    printf "${OVERWRITE}${LRED} [X]  ${TASK}${LRED}\n"
    while read line; do 
        printf "      ${line}\n"
    done < debug.log
    printf "${RESTORE}\n"
    rm debug.log
    set -o history
    exit 1
} 

sudo_check() {
 echo "Checking if sudo requires a password..."
 if sudo -n true 2>/dev/null; then
    echo "Sudo does not require a password."
 else
    echo "Sudo requires a password. Please enter your password:"
    sudo -v
    if [ $? -ne 0 ]; then
        echo "Incorrect password or sudo authentication failed."
	echo -e ""
        exit 1
    fi
 fi
 echo -e ""
}

clear_screen() {
  clear
}

check_command() {
  local command=$1
  local package=$2
  if ! command -v $command >/dev/null 2>&1; then
    echo "$command is not installed. Installing $package..."
    sudo apt-get -qq update
    sudo apt-get install -y $package
    echo "$package has been installed."
  else
    echo "$command is already installed."
  fi
}

systemctl_check() {
  local retries=3
  local delay=3
  local attempt=1
  while (( attempt <= retries )); do
      if systemctl status &>/dev/null; then
          break
      else
          echo -e "${LRED} [X] Failed to connect to systemd bus. Attempt $attempt/$retries. ${RESTORE}"
          ((attempt++))
          sleep "$delay"
      fi
  done

  if (( attempt > retries )); then
      echo -e "${LRED} [X] Failed to connect to systemd bus after $retries attempts. Operation aborted... ${RESTORE}"
      echo -e "${LRED} [i] Consider rebooting your PC if the issue persists.${RESTORE}"
      echo 
      exit 1
  fi
}

get_local_ip() {
 ip_list=($(ip addr show | grep 'inet ' | awk '{print $2}' | cut -d'/' -f1) $(hostname -I))
 ip_list=($(echo "${ip_list[@]}" | tr ' ' '\n' | sort -u | tr '\n' ' '))
 elements=${#ip_list[@]}
 choices=()
 for (( i=0; i<$elements; i++ )); do
  if [[ "$i" == "0" ]]; then
    choices+=("${ip_list[i]}" "" "ON")
  else
    choices+=("${ip_list[i]}" "" "OFF")
  fi
 done
IP=$(whiptail --title "Which IP to bind for pouwfrontend web server?" \
           --radiolist "\n\
Any IP other than 127.0.0.1 is a security risk and should be selected only by advanced users.\n\
\n\
Use the UP/DOWN arrows to highlight the IP you want.\n\
Press Spacebar on the IP you want to select, THEN press ENTER." 35 60 20 \
           "${choices[@]}" 3>&2 2>&1 1>&3)
}

daemon_setup() {
  clear_screen
  echo -e "Fluxcore for Linux AMD64 - ${GREEN}${BRANCH}${RESTORE}"
  echo "Please allow privileges to access hardware info to the program."
  sudo_check
  if ! id "fluxuser" &>/dev/null; then
    _task "Create user fluxuser"
    _cmd "sudo useradd -p '' -r -s /bin/bash -m fluxuser"
    if [[ ! -f /etc/sudoers.d/dont-prompt-fluxuser-for-sudo-password ]]; then
      _cmd "echo \"fluxuser ALL=(ALL:ALL) NOPASSWD: ALL\" | sudo tee \"/etc/sudoers.d/dont-prompt-fluxuser-for-sudo-password\" > /dev/null"
    fi
  fi
  _task "Download the Fluxcore executable"
  systemctl_check
  STATUS=$(systemctl is-active fluxcore)
  if [[ $STATUS == "active" ]]; then
    _cmd "sudo systemctl stop fluxcore.service"
    _cmd "sudo systemctl disable fluxcore.service"
  else
    PID=$(pgrep -f $name)
    if [ ! -z "$PID" ]; then
      sudo kill -9 $PID >/dev/null 2>&1
    fi
  fi
  _cmd "sudo curl -o /home/fluxuser/$name ${BASE_DOMAIN}/$name"
  _task "Set permissions for Fluxcore executable"
  _cmd "sudo chown fluxuser:fluxuser /home/fluxuser/$name && sudo chmod +x /home/fluxuser/$name"
  _cmd "sudo setcap 'CAP_DAC_READ_SEARCH+eip cap_net_bind_service=+ep' /home/fluxuser/$name"
  _cmd "sudo chmod u+s,+x /home/fluxuser/$name"
  _task "Configure and start Fluxcore service"
  if [[ -z "$IP" ]]; then
    get_local_ip
  fi
  _cmd "sudo curl -o /lib/systemd/system/fluxcore.service ${BASE_DOMAIN}/fluxcore.service"
  extra_options="-ip ${IP:-127.0.0.1}"
  if [[ -n "$EMAIL" ]]; then
    extra_options+=" -email $EMAIL"
  fi
  if [[ -n "$CLUSTER_NAME" ]]; then
    extra_options+=" -cluster $CLUSTER_NAME"
  fi
  if [[ -n "$PREMIUM_ID" ]]; then
    extra_options+=" -premium $PREMIUM_ID"
  fi
  _cmd "sudo sed -i 's|ExecStart=/bin/bash -c \"/home/fluxuser/$name -daemon -ip [0-9.]*\"|ExecStart=/bin/bash -c \"/home/fluxuser/$name -daemon $extra_options\"|' /lib/systemd/system/fluxcore.service"
  _cmd "sudo systemctl daemon-reload"
  _cmd "sudo systemctl enable fluxcore.service"
  _cmd "sudo systemctl start fluxcore.service"
  printf "${OVERWRITE}${LGREEN} [✓]  ${LGREEN}${TASK}\n ${RESTORE}"
  echo -e ""
  systemctl status --no-pager fluxcore.service
  echo

  echo -e "${PIN} ${CYAN}You can access it here: ${YELLOW}http://$IP:18180${RESTORE}"
  if [[ -n "$CLUSTER_NAME" ]]; then
   echo -e "${PIN} ${CYAN}Server assigned to cluster: ${GREEN}${CLUSTER_NAME}${RESTORE}"
  fi
  if [[ -n "$PREMIUM_ID" ]]; then
   echo -e "${PIN} ${CYAN}Machine marked as premium, ID: ${LGREEN}${PREMIUM_ID}${RESTORE}"
  fi
  echo -e "${PIN} ${CYAN}Be aware to sign in, third parties like Github, Google, etc. require you to have a domain name.${RESTORE}"
  echo -e "${PIN} ${CYAN}You can add the IP of your remote to the host file under the name 'machine1.remote.fluxcore', 'machine2.remote.fluxcore', ... ${RESTORE}"
  echo
}


uninstall() {
 clear_screen
 echo "Please allow privileges to remove program"
 sudo_check

 if sudo test -f "/home/fluxuser/$name"; then
   _task "Remove application and settings from computer and server"
   _cmd "sudo /home/fluxuser/$name -uninstall > /dev/null 2>&1"
 fi
 
 systemctl_check
 STATUS=$(systemctl is-active fluxcore)
 if [[ $STATUS == "active" ]]; then
   _cmd "sudo systemctl stop fluxcore.service"
   _cmd "sudo systemctl disable fluxcore.service"
 fi
 
 if sudo test -f /lib/systemd/system/fluxcore.service ; then
   _task "Removing fluxcore service" 
   _cmd "sudo rm /lib/systemd/system/fluxcore.service"
 fi

 if sudo test -f "/home/fluxuser/$name"; then
   _task "Removing fluxcore binary" 
   _cmd "sudo rm /home/fluxuser/$name"
 fi

 _task "Reloading systemctl daemon" 
 _cmd "sudo systemctl daemon-reload"
 if [[ -d  /home/fluxuser ]]; then
   _task "Removing user fluxuser" 
   _cmd "sudo deluser --remove-home fluxuser"
 fi

  _task "Removing Kubernetes and related files..." 
 if [[ -f /usr/local/bin/rke2-uninstall.sh ]]; then
   sudo /usr/local/bin/rke2-uninstall.sh
 fi
 sudo rm -rf /etc/ceph \
 /etc/cni \
 /etc/kubernetes \
 /etc/rancher \
